               allowedClasses = align-left,align-center,align-right,align-justify,indent,outdent   
               #allowedClassesTable = test
               
        entryHTMLparser_db = 1
        entryHTMLparser_db{
                  allowTags < RTE.default.proc.allowTags
                  allowedClasses < RTE.default.proc.allowedClasses
                  
                  denyTags >
                  htmlSpecialChars = 0
                  removeTags =  o:p, sdfield, strike, u
                  removeTags := removeFromList(font)
                  keepNonMatchedTags = protect

               
               tags{
                  img.allowedAttrib = 0
                  img.rmTagIfNoAttrib = 1
		span.fixAttrib.style.unset >
		font.fixAttrib.style.unset > 
		 #p.fixAttrib.align.unset = xml:lang
		p.fixAttrib.align.unset >
		p.allowed.Attribs = class, align, style
                  div.fixAttrib.align.unset = 1
		hr.allowedAttribs = class
		b.allowedAttribs = xml:lang
		blockquote.allowedAttribs = xml:lang
		cite.allowedAttribs = xml:lang
		em.allowedAttribs = xml:lang
		i.allowedAttribs = xml:lang
		q.allowedAttribs = xml:lang
		strong.allowedAttribs = xml:lang
		sub.allowedAttribs = xml:lang
		sup.allowedAttribs = xml:lang
		tt.allowedAttribs = xml:lang
                  
		  }
                              
        
    }
    
